import React, { useState, useEffect } from "react";
import { logIn, isLoggedIn } from "./../../APICalls/APICalls.js";
import {
  AppBar,
  Toolbar,
  makeStyles,
  Link,
  Typography,
} from "@material-ui/core";

import SpaceElement from "../SpaceElement/SpaceElement";

const backEndURLLOGIN = "http://localhost:8080/api/login";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  element: {
    margin: theme.spacing(2),
  },
}));

function Header(props) {
  const classes = useStyles();
  const [hasbeenLoggedIn, setLoggedIn] = useState(0);

  function logincallback(val) {
    console.log("callback was called");
    setLoggedIn(val);
  }
  useEffect(() => {
    isLoggedIn(logincallback);
  });

  if (hasbeenLoggedIn === 0) {
    return <h1>LOADING</h1>;
  }

  return (
    <AppBar position="static" className={classes.root}>
      <Toolbar>
        <Typography className={classes.element}>Codechef</Typography>
        <Typography className={classes.element}>Contests</Typography>
        <SpaceElement />
        <Typography>
          <Link
            color="inherit"
            href={backEndURLLOGIN}
            className={classes.element}
          >
            {(() => {
              if (hasbeenLoggedIn === 1) {
                return "logout";
              } else {
                return "Login";
              }
            })()}
          </Link>
        </Typography>
      </Toolbar>
    </AppBar>
  );
}

export default Header;
