import React, { useState, useEffect } from "react";
import { logIn, isLoggedIn } from "../../APICalls/LoginHandler.js";
import { getContestList } from "./../../APICalls/API";
import { Button, Link } from "@material-ui/core";
import Header from "../../components/Header";
import { Redirect } from "react-router-dom";

export default function ContestPage(props) {
  const [hasbeenLoggedIn, setLoggedIn] = useState(0);

  function logincallback(val) {
    setLoggedIn(val);
  }

  function handleLogout() {
    logincallback(1);
  }

  useEffect(() => {
    isLoggedIn(logincallback);
  });

  function contestListCallback(contestlist) {
    console.log("Finally got called back");
    console.log(contestlist);
  }

  useEffect(() => {
    getContestList(contestListCallback);
  }, []);

  if (hasbeenLoggedIn === 0) {
    return <h1>LOADING</h1>;
  }

  if (hasbeenLoggedIn === 1) {
    return <Redirect to="/"></Redirect>;
  }

  return (
    <React.Fragment>
      <Header hasbeenLoggedIn={hasbeenLoggedIn} logoutHandler={handleLogout} />

      <h1>This is now the contest page.</h1>
    </React.Fragment>
  );
}
