import React from "react";
import {
  AppBar,
  Toolbar,
  makeStyles,
  Link,
  Typography,
  Button,
} from "@material-ui/core";
import { logoutUser } from "../../APICalls/LoginHandler";
import SpaceElement from "../SpaceElement/SpaceElement";
import { Redirect } from "react-router-dom";

const backEndURLLOGIN = "http://localhost:8080/api/login";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  element: {
    margin: theme.spacing(2),
  },
}));

function Header(props) {
  const classes = useStyles();

  async function logOutUser() {
    await logoutUser();
    return <Redirect to="/" />;
  }

  return (
    <AppBar position="static" className={classes.root}>
      <Toolbar>
        <Typography className={classes.element}>Codechef</Typography>
        <Typography className={classes.element}>
          <Button href="/contests" color="secondary">
            Contests
          </Button>
        </Typography>
        <SpaceElement />
        <Typography>
          {(() => {
            if (props.hasbeenLoggedIn === 2) {
              return (
                <Button
                  color="inherit"
                  onClick={logOutUser}
                  className={classes.element}
                >
                  {" "}
                  logout{" "}
                </Button>
              );
            } else {
              return (
                <Button
                  color="inherit"
                  href={backEndURLLOGIN}
                  className={classes.element}
                >
                  {" "}
                  login{" "}
                </Button>
              );
            }
          })()}
        </Typography>
      </Toolbar>
    </AppBar>
  );
}

export default Header;
