import React from "react";
import {
  Grid,
  TextareaAutosize,
  makeStyles,
  FormControl,
  MenuItem,
  Select,
  InputLabel,
  FormHelperText,
  Button,
} from "@material-ui/core";
import LanguageSelector from "./LanguageSelector";
import { submitCode } from "./../../APICalls/API";

const useStyles = makeStyles((theme) => ({
  textField: {
    width: "500px",
  },
}));

export default function OnlineEditor(props) {
  const classes = useStyles();

  const [values, setValues] = React.useState({
    sourceCode: "",
    testCase: "",
    languageSelected: props.languages[0].shortName,
  });

  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };

  function runProblemCallBack(data) {}

  function runProblem() {
    submitCode(
      runProblemCallBack,
      values.sourceCode,
      values.testCase,
      values.languageSelected
    );
  }

  return (
    <Grid container direction="column">
      <Grid item></Grid>
      <Grid item xs={8}>
        <br />
        <h2>Problem Testing</h2>
      </Grid>
      <Grid item>
        <LanguageSelector
          languages={props.languages}
          selectedLanguage={values.languageSelected}
          onChange={handleChange("languageSelected")}
        />
      </Grid>
      <Grid container>
        <Grid item>
          <Grid container direction="column">
            <Grid item>
              <h5>Source Code</h5>
            </Grid>
            <Grid item>
              <TextareaAutosize
                className={classes.textField}
                onChange={handleChange("sourceCode")}
              />
            </Grid>
          </Grid>
        </Grid>
        <Grid item>
          <Grid container direction="column">
            <Grid item>
              <h5>Test Case</h5>
            </Grid>
            <Grid item>
              <TextareaAutosize
                className={classes.textField}
                onChange={handleChange("testCase")}
              />
            </Grid>
          </Grid>
        </Grid>
      </Grid>
      <Button color="inherit" onClick={runProblem}>
        Submit
      </Button>
      <textarea className={{ flexGrow: 1 }}>
        Lorem ipsum dolor sit amet consectetur adipisicing elit. Praesentium
        quia culpa, dolorum ab suscipit consequatur, quod molestiae aliquid
        aliquam odio inventore quo sunt explicabo atque illum necessitatibus
        voluptates nam rem non, consectetur earum laborum expedita iure tempora?
        Sint laborum tempora laboriosam neque aspernatur non nemo adipisci
        magnam, esse, reiciendis a? Ducimus saepe id porro, consequatur dolorum
        quaerat error iusto quisquam nulla voluptatum mollitia, culpa reiciendis
        quasi placeat delectus tempora officiis. Accusantium repudiandae omnis
        praesentium accusamus inventore quam corrupti ratione quis voluptas
        consequuntur aspernatur vero natus possimus impedit minus ipsam nisi
        quidem reiciendis perferendis deleniti, atque consequatur. Omnis illum
        obcaecati eius!
      </textarea>
    </Grid>
  );
}
