import React, { useState, useEffect } from "react";
import { isLoggedIn } from "../../APICalls/LoginHandler";
import { getProblemDetails, getSubmissionForProblem } from "../../APICalls/API";
import { Redirect } from "react-router-dom";
import StickyHeadTable from "./Table";
<OutlinedInput
  id="outlined-adornment-amount"
  value={values.amount}
  onChange={handleChange("amount")}
  startAdornment={<InputAdornment position="start">$</InputAdornment>}
  labelWidth={60}
/>;
import Header from "../../components/Header";
import {
  Link,
  Grid,
  Typography,
  TextareaAutosize,
  TextField,
} from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    flexWrap: "wrap",
  },
  margin: {
    margin: theme.spacing(1),
  },
  withoutLabel: {
    marginTop: theme.spacing(3),
  },
  textField: {
    display: "flex",
    flexGrow: 1,
    //width: "400px",
  },
}));

export default function SpecificContestPage(props) {
  const classes = useStyles();
  const [hasbeenLoggedIn, setLoggedIn] = useState(0);
  const [problemDetails, setProblemDetails] = useState(0);
  const [submissions, setSubmission] = useState(0);

  function logincallback(val) {
    setLoggedIn(val);
  }

  function handleLogout() {
    logincallback(1);
  }

  useEffect(() => {
    isLoggedIn(logincallback);
  });

  function problemDetailsCallback(problemDetails) {
    console.log("Finally got called back in problem display page");
    console.log(problemDetails);
    setProblemDetails(problemDetails);
  }

  function getSubmissionsCallBack(submissions) {
    setSubmission(submissions.content);
  }

  useEffect(() => {
    getProblemDetails(
      problemDetailsCallback,
      props.match.params.contestCode,
      props.match.params.problemCode
    );
    getSubmissionForProblem(
      getSubmissionsCallBack,
      props.match.params.problemCode
    );
  }, []);

  if (hasbeenLoggedIn === 1) {
    return <Redirect to="/"></Redirect>;
  }

  if (hasbeenLoggedIn === 0 || problemDetails === 0 || submissions === 0) {
    return <h1>LOADING</h1>;
  }

  if (problemDetails === null) {
    return <h1>Error occurred, Refresh Page</h1>;
  }

  return (
    <React.Fragment>
      <Header hasbeenLoggedIn={hasbeenLoggedIn} logoutHandler={handleLogout} />
      <Link href={"/contestpage/" + props.match.params.contestCode}>
        Go Back to Contest
      </Link>
      <Grid container>
        <Grid item xs={false} sm={1}></Grid>
        <Grid item xs={10}>
          <Grid container direction="column">
            <Grid item>
              <Grid container spacing={2} align-content={"space-between"}>
                <Grid item xs={12} sm={7}>
                  <Typography variant="h1">
                    {problemDetails.problemName}
                  </Typography>
                  <Typography>{problemDetails.body}</Typography>
                </Grid>
                <Grid item xs={12} sm={5}>
                  {(() => {
                    if (submissions !== undefined)
                      return <StickyHeadTable submissions={submissions} />;
                  })()}
                </Grid>
              </Grid>
            </Grid>
            <Grid item xs={2}>
              Enter your code here
            </Grid>
            <Grid item>
              <OutlinedInput
                id="outlined-adornment-amount"
                value={values.amount}
                startAdornment={
                  <InputAdornment position="start">$</InputAdornment>
                }
                labelWidth={60}
              />
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </React.Fragment>
  );
}
