import React from "react";
import { Grid, TextareaAutosize, makeStyles } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  textField: {
    width: "500px",
  },
}));

export default function OnlineEditor() {
  const classes = useStyles();
  const [values, setValues] = React.useState({
    amount: "",
    password: "",
    weight: "",
    weightRange: "",
    showPassword: false,
  });

  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };
  return (
    <Grid container direction="column">
      <Grid item xs={8}>
        <br />
        <h2>Problem Testing</h2>
      </Grid>
      <Grid container>
        <Grid item>
          <Grid container direction="column">
            <Grid item>
              <h5>Source Code</h5>
            </Grid>
            <Grid item>
              <TextareaAutosize className={classes.textField} />
            </Grid>
          </Grid>
        </Grid>
        <Grid item>
          <Grid container direction="column">
            <Grid item>
              <h5>Test Case</h5>
            </Grid>
            <Grid item>
              <TextareaAutosize className={classes.textField} />
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  );
}
