import React, { useState, useEffect } from "react";
import { Button, Link } from "@material-ui/core";
import Header from "../../components/Header";
import { logIn, isLoggedIn } from "../../APICalls/LoginHandler.js";
import axios from "axios";

export default function Welcome() {
  const [hasbeenLoggedIn, setLoggedIn] = useState(0);

  function logincallback(val) {
    setLoggedIn(val);
  }

  function logoutHandler() {
    logincallback(1);
  }

  useEffect(() => {
    isLoggedIn(logincallback);
  }, [hasbeenLoggedIn]);

  return (
    <React.Fragment>
      <Header hasbeenLoggedIn={hasbeenLoggedIn} logoutHandler={logoutHandler} />
    </React.Fragment>
  );
}
