import React, { Component } from "react";
import Counter from "./Counter";

class CounterPanel extends Component {
  state = {
    internalCounter: 1,
    allCounters: [<Counter id={internalCounter} />],
  };

  render() {
    return (
      <div>
        <button onClick={this.createNewCounter}>Create New Counter</button>

        <ul>
          {this.state.allCounters.map((counter) => {
            return counter;
          })}
        </ul>
      </div>
    );
  }

  createNewCounter = () => {
    console.log(1);
    //const { allCounters } = this.state;
    //allCounters.push(<Counter />);
    //allCounters.pop();
    const allC = [...this.state.allCounters];
    allC.push(<Counter id={this.state.internalCounter} />);
    const [firstEl, ...rest] = allC;
    this.setState({
      allCounters: allC,
      internalCounter: this.state.internalCounter + 1,
    });
    //this.setState({ internalCounter: this.state.internalCounter + 1 });
  };
}

export default CounterPanel;
