import React, { useState } from "react";
import {
  makeStyles,
  FormControl,
  MenuItem,
  Select,
  InputLabel,
  FormHelperText,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

export default function LanguageSelector(props) {
  const classes = useStyles();
  const [value, setValue] = useState(props.languages[0].shortName);
  console.log(props.languages[0].shortName);
  function handleChange(event) {
    setValue(event.target.value);
  }

  return (
    <React.Fragment>
      <FormControl required className={classes.formControl}>
        <InputLabel id="demo-simple-select-required-label">Lang</InputLabel>
        <Select
          labelId="demo-simple-select-required-label"
          id="demo-simple-select-required"
          value={value}
          onChange={handleChange}
          className={classes.selectEmpty}
        >
          {props.languages.map((language) => {
            return (
              <MenuItem key={language.shortName} value={language.shortName}>
                {language.shortName}
              </MenuItem>
            );
          })}
        </Select>
        <FormHelperText>Required</FormHelperText>
      </FormControl>
    </React.Fragment>
  );
}
