import React, { useState, useEffect } from "react";
import { Button, Link } from "@material-ui/core";
import Header from "../../components/Header";
import { logIn, isLoggedIn } from "../../APICalls/LoginHandler.js";
import axios from "axios";

export default function Welcome() {
  const [hasbeenLoggedIn, setLoggedIn] = useState(0);

  function logincallback(val) {
    setLoggedIn(val);
  }

  function logoutHandler() {
    logincallback(1);
  }

  useEffect(() => {
    isLoggedIn(logincallback);
  });

  function displayUser() {
    const baseurl = "http://localhost:8080/api/userinfo";
    axios.get(baseurl, { withCredentials: true }).then((response) => {
      console.log(response);
    });
  }

  return (
    <React.Fragment>
      <Header hasbeenLoggedIn={hasbeenLoggedIn} logoutHandler={logoutHandler} />
      <Button onClick={displayUser}> Set Name</Button>
      <Button> get Name</Button>
      <Button> del Name</Button>
    </React.Fragment>
  );
}
