import React, { useState, useEffect } from "react";
import { isLoggedIn } from "../../APICalls/LoginHandler";
import { getContestDetails } from "../../APICalls/API";
import { Redirect } from "react-router-dom";
import ContestDisplay from "../../components/DisplayTable";
import Header from "../../components/Header";
import { Grid } from "@material-ui/core";
import RanklistDisplay from "../../components/RanklistDisplay/RanklistDisplay";
import SubmissionInfoDisplay from "../../components/SubmissionInfoDisplay/SubmissoinInfoDisplay";

export default function ContestDisplayPage(props) {
  const [hasbeenLoggedIn, setLoggedIn] = useState(0);

  function logincallback(val) {
    setLoggedIn(val);
  }

  function handleLogout() {
    logincallback(1);
  }

  useEffect(() => {
    isLoggedIn(logincallback);
  });

  if (hasbeenLoggedIn === 0) {
    return <h1>LOADING</h1>;
  }

  if (hasbeenLoggedIn === 1) {
    return <Redirect to="/"></Redirect>;
  }

  return (
    <React.Fragment>
      <Header hasbeenLoggedIn={hasbeenLoggedIn} logoutHandler={handleLogout} />
      <Grid container>
        <Grid item xs={false} sm={1} md={2}></Grid>
        <Grid item xs={12} sm={10} md={8}>
          <Grid container direction="column" spacing={2}>
            <Grid item>
              <ContestDisplay contestcode />
            </Grid>
            <Grid container direction="row" spacing={2}>
              <Grid item xs={6}>
                <RanklistDisplay />
              </Grid>
              <Grid item xs={6}>
                <SubmissionInfoDisplay />
              </Grid>
            </Grid>
          </Grid>
        </Grid>

        <Grid item xs={false} sm={1} md={2}></Grid>
      </Grid>
    </React.Fragment>
  );
}
