import axios from "axios";
import { isValid } from "./API";

const backendURL = "http://localhost:8080/api";

let userinfo = { username: undefined };

export async function logoutUser() {
  axios
    .get(backendURL + "/logout", { withCredentials: true })
    .then((response) => {
      localStorage.clear();
      userinfo.username = "";
      console.log(response);
    });
}

export function getUserDetails(callback) {
  if (userinfo.username !== "") {
    callback(userinfo.username);
    return;
  }
  console.log("OOF");
  axios
    .get(backendURL + "/userinfo", { withCredentials: true })
    .then((response) => {
      if (!isValid(response)) userinfo.username = "";
      else userinfo.username = response.data.username;
      console.log("WOW");
      console.log();
      console.log(userinfo.username);
      callback(userinfo.username);
    });
}

export function isLoggedIn() {
  if (userinfo.username !== "") return true;
  else return false;

