import React from "react";
import { AppBar, Toolbar, makeStyles, Button } from "@material-ui/core";
import HeaderLabel from "./HeaderLabel";
import SpaceElement from "../SpaceElement/SpaceElement";
import axios from "axios";

const testURL = "http://localhost:8080/api/homepage";
const baseURL = "http://localhost:8080/api";
const clientID = "2772597d9ced75b5d8fc0e0a569e27c2";
const CC_URL = "https://api.codechef.com/oauth/authorize";
const REDIRECT_URI = "http://localhost:8080/api/login/keydetails";

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  title: {
    margin: theme.spacing(2),
  },
}));

function doOnClick() {
  const toPass = {
    withCredentials: true,
    headers: {
      "Access-Control-Allow-Origin": "http://127.0.0.1:3000",
      "Content-Type": "application/json",
    },
  };
  //console.log("This link has been pressed");
  let isLoggedin = false;
  axios
    .get(
      baseURL + "/loggedinstatus",
      "This is a sample message that has been passed",
      toPass
    )
    .then((response) => {
      console.log("HERE");
      if (response.data === "y") isLoggedin = true;
      if (!isLoggedin) {
        console.log("yay");
        axios
          .get(CC_URL, {
            params: {
              response_type: "code",
              client_id: clientID,
              redirect_uri: REDIRECT_URI,
              state: "xyz",
            },
          })
          .then((response) => {
            console.log("DID AUTH YAY");
          });
      }
    })
    .catch((Error) => {
      console.log(Error);
    });
}

function Header() {
  const classes = useStyles();
  return (
    <AppBar position="static" className={classes.root}>
      <Toolbar>
        <HeaderLabel>Codechef</HeaderLabel>
        <HeaderLabel link="/contests">Contests</HeaderLabel>
        <SpaceElement />
        <HeaderLabel>
          <Button color="inherit" onClick={doOnClick}>
            Login
          </Button>
        </HeaderLabel>
      </Toolbar>
    </AppBar>
  );
}

export default Header;
