import React, { useState, useEffect } from "react";
import { logIn, isLoggedIn } from "./../../APICalls/APICalls.js";
import { Button, Link } from "@material-ui/core";
import Header from "../../components/Header";

export default function ContestPage() {
  const [hasbeenLoggedIn, setLoggedIn] = useState(0);

  function logincallback(val) {
    console.log("callback was called");
    setLoggedIn(val);
  }
  useEffect(() => {
    isLoggedIn(logincallback);
  });

  if (hasbeenLoggedIn === 0) {
    return <h1>LOADING</h1>;
  }

  return (
    <React.Fragment>
      <Header hasbeenLoggedIn={hasbeenLoggedIn} />
      <br />
      <h1>This is now the contest page.</h1>
    </React.Fragment>
  );
}
