import React from "react";
import {
  Grid,
  TextareaAutosize,
  makeStyles,
  FormControl,
  MenuItem,
  Select,
  InputLabel,
  FormHelperText,
} from "@material-ui/core";
import LanguageSelector from "./LanguageSelector";

const useStyles = makeStyles((theme) => ({
  textField: {
    width: "500px",
  },
}));

export default function OnlineEditor(props) {
  const classes = useStyles();

  const [values, setValues] = React.useState({
    sourceCode: "",
    testCase: "",
    languageSelected: props.languages[0].shortName,
  });

  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };

  return (
    <Grid container direction="column">
      <Grid item></Grid>
      <Grid item xs={8}>
        <br />
        <h2>Problem Testing</h2>
      </Grid>
      <Grid item>
        <LanguageSelector
          languages={props.languages}
          selectedLanguage={languageSelected}
        />
      </Grid>
      <Grid container>
        <Grid item>
          <Grid container direction="column">
            <Grid item>
              <h5>Source Code</h5>
            </Grid>
            <Grid item>
              <TextareaAutosize
                className={classes.textField}
                onChange={handleChange("sourceCode")}
              />
            </Grid>
          </Grid>
        </Grid>
        <Grid item>
          <Grid container direction="column">
            <Grid item>
              <h5>Test Case</h5>
            </Grid>
            <Grid item>
              <TextareaAutosize
                className={classes.textField}
                onChange={handleChange("testCase")}
              />
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </Grid>
  );
}
