import React, { Component } from "react";
import Counter from "./Counter";

class CounterPanel extends Component {
  state = {
    allCounters: [<Counter />],
  };

  render() {
    return (
      <div>
        <button onClick={this.createNewCounter}>Create New Counter</button>

        <ul>
          {this.state.allCounters.map((counter) => {
            return counter;
          })}
        </ul>
      </div>
    );
  }

  createNewCounter = () => {
    this.state.allCounters.append(<Counter />);
  };
}

export default CounterPanel;
