import React, { useState, useEffect } from "react";
import { isLoggedIn } from "./../../APICalls/LoginHandler";
import { getContestDetails } from "./../../APICalls/API";

export default function SpecificContestPage(props) {
  const [hasbeenLoggedIn, setLoggedIn] = useState(0);
  const [contestList, setContestList] = useState([]);

  function logincallback(val) {
    setLoggedIn(val);
  }

  function handleLogout() {
    logincallback(1);
  }

  useEffect(() => {
    isLoggedIn(logincallback);
  });

  function contestDetailsCallback(contestDetails) {
    console.log("Finally got called back");
    console.log(contestDetails);
    setContestList(contestlist.contestList);
  }

  useEffect(() => {
    getContestDetails(contestDetailsCallback, props.match.params.handle);
  }, []);

  if (hasbeenLoggedIn === 0) {
    return <h1>LOADING</h1>;
  }

  if (hasbeenLoggedIn === 1) {
    return <Redirect to="/"></Redirect>;
  }

  return (
    <React.Fragment>
      <Header hasbeenLoggedIn={hasbeenLoggedIn} logoutHandler={handleLogout} />
      <ContestSelector contestList={contestList} />
    </React.Fragment>
  );
}
