import React, { useState } from "react";
import {
  Grid,
  TextareaAutosize,
  makeStyles,
  FormControl,
  MenuItem,
  Select,
  InputLabel,
  FormHelperText,
  Button,
} from "@material-ui/core";
import LanguageSelector from "./LanguageSelector";
import { submitCode, getProblemStatus } from "./../../APICalls/API";
import createPalette from "@material-ui/core/styles/createPalette";

// #include <iostream>
// using namespace std;
// int main(cout<<"hih" <<endl;return 0;}

const useStyles = makeStyles((theme) => ({
  textField: {
    width: "500px",
  },
}));

export default function OnlineEditor(props) {
  const classes = useStyles();
  const [submitLink, setSubmitLink] = useState(0);
  const [values, setValues] = useState({
    sourceCode: "",
    testCase: "",
    languageSelected: props.languages[0].shortName,
    output: "",
    status: "None Submitted",
    cmpinfo: "",
  });

  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };

  function getStatus(status) {
    //console.log("THIS IS THE STATUS");
    //return;
    console.log(status);
    if (status === undefined) {
      getProblemStatus(getStatus, submitLink);
    } else {
      setValues({
        ...values,
        status: "Evaluated",
        output: status.output,
        cmpinfo: status.cmpinfo,
      });
    }
  }

  function runProblemCallBack(data) {
    setValues({ ...values, status: "submitted" });
    console.log(data);
    if (data.link !== undefined) {
      setSubmitLink(data.link);
      getProblemStatus(getStatus, data.link);
    }
  }
  function runProblem() {
    if (values.sourceCode === "") {
      setValues({ ...values, status: "Cannot Submit Empty Code" });
      return;
    }
    submitCode(
      runProblemCallBack,
      values.sourceCode,
      values.testCase,
      values.languageSelected
    );
  }

  return (
    <Grid container direction="column">
      <Grid item></Grid>
      <Grid item xs={8}>
        <br />
        <h2>Problem Testing</h2>
      </Grid>
      <Grid item>
        <LanguageSelector
          languages={props.languages}
          selectedLanguage={values.languageSelected}
          onChange={handleChange("languageSelected")}
        />
      </Grid>
      <Grid container>
        <Grid item>
          <Grid container direction="column">
            <Grid item>
              <h5>Source Code</h5>
            </Grid>
            <Grid item>
              <TextareaAutosize
                className={classes.textField}
                onChange={handleChange("sourceCode")}
              />
            </Grid>
          </Grid>
        </Grid>
        <Grid item>
          <Grid container direction="column">
            <Grid item>
              <h5>Test Case</h5>
            </Grid>
            <Grid item>
              <TextareaAutosize
                className={classes.textField}
                onChange={handleChange("testCase")}
              />
            </Grid>
          </Grid>
        </Grid>
      </Grid>
      <Button color="inherit" onClick={runProblem}>
        Submit
      </Button>
      <h5>Status: {values.status}</h5>
      <textarea className={{ flexGrow: 1 }} rows={7}>
        {values.output}
      </textarea>
      <textarea
        className={{ flexGrow: 1 }}
        rows={7}
        //value={values.cmpinfo}
      >
        {values.cmpinfo}
      </textarea>
    </Grid>
  );
}
