import React, { useState } from "react";
import {
  Grid,
  TextareaAutosize,
  makeStyles,
  FormControl,
  MenuItem,
  Select,
  InputLabel,
  FormHelperText,
  Button,
} from "@material-ui/core";
import LanguageSelector from "./LanguageSelector";
import { submitCode } from "./../../APICalls/API";

const useStyles = makeStyles((theme) => ({
  textField: {
    width: "500px",
  },
}));

export default function OnlineEditor(props) {
  const classes = useStyles();

  const [values, setValues] = useState({
    sourceCode: "",
    testCase: "",
    languageSelected: props.languages[0].shortName,
    output: "",
    status: "None Submitted",
  });

  const handleChange = (prop) => (event) => {
    setValues({ ...values, [prop]: event.target.value });
  };

  function runProblemCallBack(data) {
    setValues({ ...values, status: "submitted" });
  }

  function runProblem() {
    if (values.sourceCode === "") {
      setValues({ ...values, status: "Cannot Submit Empty Code" });
      return;
    }
    submitCode(
      runProblemCallBack,
      values.sourceCode,
      values.testCase,
      values.languageSelected
    );
  }

  return (
    <Grid container direction="column">
      <Grid item></Grid>
      <Grid item xs={8}>
        <br />
        <h2>Problem Testing</h2>
      </Grid>
      <Grid item>
        <LanguageSelector
          languages={props.languages}
          selectedLanguage={values.languageSelected}
          onChange={handleChange("languageSelected")}
        />
      </Grid>
      <Grid container>
        <Grid item>
          <Grid container direction="column">
            <Grid item>
              <h5>Source Code</h5>
            </Grid>
            <Grid item>
              <TextareaAutosize
                className={classes.textField}
                onChange={handleChange("sourceCode")}
              />
            </Grid>
          </Grid>
        </Grid>
        <Grid item>
          <Grid container direction="column">
            <Grid item>
              <h5>Test Case</h5>
            </Grid>
            <Grid item>
              <TextareaAutosize
                className={classes.textField}
                onChange={handleChange("testCase")}
              />
            </Grid>
          </Grid>
        </Grid>
      </Grid>
      <Button color="inherit" onClick={runProblem}>
        Submit
      </Button>
      <h5>Status: {values.status}</h5>
      <textarea
        className={{ flexGrow: 1 }}
        rows={7}
        value={values.output}
      ></textarea>
    </Grid>
  );
}
