import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import Header from "../../components/Header";
import axios from "axios";

class Welcome extends Component {
  toPass = {
    withCredentials: true,
    headers: {
      "Access-Control-Allow-Origin": "*",
      "Content-Type": "application/json",
    },
  };

  setSessionVariable() {
    const URL = "http://localhost:8080/test/ssn";
    axios.defaults.withCredentials = true;
    axios
      .get(URL, "This is a sample message that has been passed", this.toPass)
      .then((response) => {
        console.log("HERE1");
        console.log(response);
      })
      .catch((Error) => {
        console.log(Error);
      });
  }

  getSessionVariable() {
    const URL = "http://localhost:8080/test/gsn";
    //axios.defaults.withCredentials = true;
    axios
      .get(URL, "This is a sample message that has been passed", this.toPass)
      .then((response) => {
        console.log("HERE2");
        console.log(response);
      })
      .catch((Error) => {
        console.log(Error);
      });
  }
  delSessionVariable = () => {
    const URL = "http://localhost:8080/test/dsn";
    //axios.defaults.withCredentials = true;
    axios
      .get(URL, "This is a sample message that has been passed", this.toPass)
      .then((response) => {
        console.log("HERE3");
        console.log(response);
      })
      .catch((Error) => {
        console.log(Error);
      });
  };

  render() {
    return (
      <React.Fragment>
        <Header />
        <Button onClick={this.setSessionVariable}> Set Name</Button>
        <Button onClick={this.getSessionVariable}> get Name</Button>
        <Button onClick={this.delSessionVariable}> del Name</Button>
      </React.Fragment>
    );
  }
}

export default Welcome;
